import React from 'react'
import Divider from './Divider'
import Button from './Button'

export default function CaseStudyCard( {type, projectName, projectDescription, projectScope, projectPlatforms, projectTimeline, link, projectImage, bgDirection}) {

    if ( type === "Secondary") {
        return (
            <div
                className="bento-card case-study-bento-card"
                style = {{
                    background: `url(${projectImage}) ${bgDirection} center`,
                    backgroundSize: 'cover'
                }}>
            </div>
        )
    }

    else {
        return (
            <div className="case-study-card">

                <div className="case-study-card-left">
                    <div className="project-header">
                        <h1 className="project-title">{projectName}</h1>
                        <Divider />
                        <p>{projectDescription}</p>
                    </div>

                    <div className="project-info">
                        <Divider />
                        {<p>Scope | {projectScope}</p>}
                        {<p>Platforms | {projectPlatforms}</p>}
                        {<p>Timeline | {projectTimeline}</p>}
                        <Divider />
                    </div>

                    <Button text="View case study" link={link} />

                </div>

                <div className="case-study-card-right">
                    <img src={projectImage} className="case-study-image" />

                </div>

            </div>
        )
    }
}
