import React from 'react'
import Divider from './Divider'
import Button from './Button'
import { Link } from 'react-router-dom'

export default function CaseStudyCard( {type, projectName, projectDescription, projectScope, projectPlatforms, projectTimeline, link, projectImage, bgDirection, showCTA=true}) {


    if ( type === "Secondary") {
        return (
            <Link to={link} style = { {Â width: '100%'}}>
                <div
                    className="bento-card case-study-bento-card"
                    style = {{
                        background: `url(${projectImage}) ${bgDirection} center`,
                        backgroundSize: 'cover',
                        width: '100%'
                    }}
                >
                </div>
            </Link>
        )
    }

    else {
        return (
            <Link to={link}>
                <div className="case-study-card">

                    <div className="case-study-card-left">
                        <div className="project-header">
                            <h1 className="project-title">{projectName}</h1>
                            <Divider />
                            <p>{projectDescription}</p>
                        </div>

                        <div className="project-info">
                            <Divider />
                            {<p>Scope | {projectScope}</p>}
                            {<p>Platforms | {projectPlatforms}</p>}
                            {<p>Timeline | {projectTimeline}</p>}
                            <Divider />
                        </div>

                        { showCTA ? <Button text="View case study" link={link} /> : null }

                    </div>

                    <div className="case-study-card-right">
                        <img src={projectImage} alt={`${projectName}'s case study image`}className="case-study-image" />

                    </div>

                </div>
            </Link>
        )
    }
}
